export function smoothScroll(){const e=document.querySelectorAll('a[href^="#"]');for(let o of e)o.addEventListener("click",(function(e){e.preventDefault();const n=o.getAttribute("href"),r=document.querySelector(""+n).getBoundingClientRect().top-t(document.querySelector(".header"));window.scrollBy({behavior:"smooth",top:r})}));function t(e){let t=0;return window.matchMedia("(max-width: 1199px)").matches&&(t=e.offsetHeight),t}}